@use 'config' as c;
@use 'screen' as s;

[data-aos] {
  body[data-aos-easing='new-easing'] &,
  &[data-aos][data-aos-easing='new-easing'] {
    transition-timing-function: cubic-bezier(0.25, 0.25, 0.75, 0.75);
  }
}
body {
  background: white;
  font-family: 'Nunito', sans-serif;
}

.header {
  position: fixed;
  // height: 58px;
  width: 100%;
  // height: 100vh;
  top: 0;
  font-family: 'Plus Jakarta Display', sans-serif;
  background: c.$sec-color;
  padding: 12px 0;
  z-index: 12;
  &.bottom-border {
    box-shadow: -3px 3px 8px grey;
  }
  & .header-items {
    max-width: 1400px;
    width: 90%;
    margin: 0 auto;
    color: c.$dark-color;
    @include c.flex(row);

    & #logo {
      font-size: map-get(c.$font-sizes, main-contents);
      font-weight: map-get(c.$font-weight, bold);
      cursor: pointer;
      @include c.transition;

      &:hover {
        color: c.$dark-milk;
      }
    }

    & #nav-list {
      @include c.flex(row);
      width: 400px;

      & .nav-item {
        position: relative;
        font-size: map-get(c.$font-sizes, nav-footer-texts);

        &:first-child:after {
          position: absolute;
          content: '';
          bottom: -2px;
          left: 50%;
          transform: translateX(-50%);
          width: 75%;
          height: 2px;
          background: c.$dark-color;
        }

        &:before {
          position: absolute;
          content: '';
          bottom: -2px;
          left: 50%;
          transform: translateX(-50%);
          width: 0;
          height: 2px;
          background: c.$dark-color;
          @include c.transition;
        }
        &:hover::before {
          width: 75%;
        }
      }
    }

    & #nav-actions {
      @include c.flex(row);
      position: relative;
      z-index: 20;
      & i {
        cursor: pointer;
        @include c.transition;

        &:hover {
          color: c.$dark-milk;
        }
      }

      & i:nth-child(2) {
        margin-left: 15px;
      }

      & .nav-toggler {
        width: 30px;
        height: 25px;
        cursor: pointer;
        @include c.flex(column);
        display: none;

        &:hover > div {
          background: c.$dark-milk;
        }

        &.open .bar {
          transform: rotate(-42deg);
        }
        &.open .start {
          transform: rotate(-3198deg) translate(20.5px, 10px);
        }
        &.open .end {
          // transform: rotate(-500deg) translate(0px, 8px);
          transform: rotate(-3198deg) translate(-20px, -10px);
        }

        & .bar {
          width: 100%;
          height: 3px;
          border-radius: 3px;
          background: c.$dark-color;
          transition: all 1550ms cubic-bezier(1, 0.1, 0.2, 1);

          &:nth-child(odd) {
            width: 60%;
          }

          &.start {
            align-self: flex-start;
            transform-origin: right;
          }
          &.end {
            align-self: flex-end;
            transform-origin: left;
          }
        }
      }
    }
  }
}

.blocks {
  display: none;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  & .block {
    position: relative;
    height: 100vh;
    width: 100px;
    background: c.$sec-color;
    border-bottom: 5px solid c.$dark-color;
    transform: translateY(-10000%);
    @include c.transition;
    transition: all 500ms cubic-bezier(0.3, 0.1, 1, 0.5);
    z-index: 10;

    &.drop {
      transform: translateY(0);
    }
  }
}

