@import url("https://fonts.googleapis.com/css2?family=Barlow+Condensed&family=Bellefair&family=Nunito:wght@400;600;700&display=swap");
@import url("https://cdn.jsdelivr.net/npm/@xz/fonts@1/serve/plus-jakarta-display.min.css");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  scroll-behavior: smooth;
}

a {
  text-decoration: none;
  color: #14120f;
}

li {
  list-style: none;
}

.header .header-items #nav-list .nav-item:nth-child(1) {
  transition-delay: 0.1s;
}

.header .header-items #nav-list .nav-item:nth-child(2) {
  transition-delay: 0.2s;
}

.header .header-items #nav-list .nav-item:nth-child(3) {
  transition-delay: 0.3s;
}

.header .header-items #nav-list .nav-item:nth-child(4) {
  transition-delay: 0.4s;
}

body[data-aos-easing=new-easing] [data-aos], [data-aos][data-aos][data-aos-easing=new-easing] {
  transition-timing-function: cubic-bezier(0.25, 0.25, 0.75, 0.75);
}

body {
  background: white;
  font-family: "Nunito", sans-serif;
}

.arrow {
  margin-top: -100px;
  width: 500px;
}
.arrow img {
  width: 100%;
}

.arrow.right {
  margin-left: auto;
}

.preloader {
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  align-self: auto;
  background: #f6eddd;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  justify-content: center;
  font-family: "Plus Jakarta Display", sans-serif;
  font-size: 60px;
  font-weight: bolder;
  color: #623b1c;
  z-index: 100;
  transform: translateY(0);
  transition: all 700ms ease-in-out;
  transition: all 10000ms ease-out;
}
.preloader.remove {
  transform: translateY(-2000%);
}
.preloader .lds-ellipsis {
  display: inline-block;
  position: relative;
  width: 80px;
  height: 80px;
}
.preloader .lds-ellipsis div {
  position: absolute;
  top: 33px;
  width: 15px;
  height: 15px;
  border-radius: 30%;
  background: #623b1c;
  animation-timing-function: cubic-bezier(0, 1, 1, 0);
}
.preloader .lds-ellipsis div:nth-child(1) {
  left: 8px;
  animation: lds-ellipsis1 0.9s infinite;
}
.preloader .lds-ellipsis div:nth-child(2) {
  left: 8px;
  animation: lds-ellipsis2 0.9s infinite;
}
.preloader .lds-ellipsis div:nth-child(3) {
  left: 32px;
  animation: lds-ellipsis2 0.9s infinite;
}
.preloader .lds-ellipsis div:nth-child(4) {
  left: 56px;
  animation: lds-ellipsis3 0.9s infinite;
}

@keyframes lds-ellipsis1 {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}
@keyframes lds-ellipsis3 {
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(0);
  }
}
@keyframes lds-ellipsis2 {
  0% {
    transform: translate(0, 0);
  }
  100% {
    transform: translate(24px, 0);
  }
}
.to-top {
  position: absolute;
  bottom: 20px;
  right: 20px;
  color: #f6eddd;
  background: #623b1c;
  border-radius: 50%;
  height: 30px;
  line-height: 30px;
  text-align: center;
  cursor: pointer;
  z-index: 5;
  position: fixed;
  animation: bulge infinite 2000ms;
  opacity: 0;
  width: 0;
  transition: all 700ms ease-in-out;
}
.to-top.show {
  opacity: 0.8;
  width: 30px;
}
.to-top i {
  font-size: 25px;
  font-weight: bold;
}

@keyframes bulge {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.2);
  }
  100% {
    transform: scale(1);
  }
}
.header {
  position: fixed;
  width: 100%;
  top: 0;
  font-family: "Plus Jakarta Display", sans-serif;
  background: #f6eddd;
  padding: 12px 0;
  z-index: 12;
}
.header.bottom-border {
  box-shadow: -3px 3px 8px grey;
}
.header .header-items {
  max-width: 1300px;
  width: 90%;
  margin: 0 auto;
  color: #14120f;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: row;
}
.header .header-items #logo {
  font-size: 24px;
  font-weight: 600;
  cursor: pointer;
  transition: all 700ms ease-in-out;
}
.header .header-items #logo:hover {
