}
.header .header-items #nav-list {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: row;
  width: 400px;
}
.header .header-items #nav-list .nav-item {
  position: relative;
  font-size: 18px;
}
.header .header-items #nav-list .nav-item.show::after {
  position: absolute;
  content: "";
  bottom: -2px;
  left: 50%;
  transform: translateX(-50%);
  width: 75%;
  height: 2px;
  background: #14120f;
}
.header .header-items #nav-list .nav-item:before {
  position: absolute;
  content: "";
  bottom: -2px;
  left: 50%;
  transform: translateX(-50%);
  width: 0;
  height: 2px;
  background: #14120f;
  transition: all 700ms ease-in-out;
}
.header .header-items #nav-list .nav-item:hover::before {
  width: 75%;
}
.header .header-items #nav-actions {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: row;
  position: relative;
  z-index: 20;
}
.header .header-items #nav-actions i {
  cursor: pointer;
  transition: all 700ms ease-in-out;
}
.header .header-items #nav-actions i:hover {
  color: #caad8c;
}
.header .header-items #nav-actions i:nth-child(2) {
  margin-left: 15px;
}
.header .header-items #nav-actions .nav-toggler {
  width: 30px;
  height: 25px;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  display: none;
}
.header .header-items #nav-actions .nav-toggler:hover > div {
  background: #caad8c;
}
.header .header-items #nav-actions .nav-toggler.open .bar {
  transform: rotate(-42deg);
}
.header .header-items #nav-actions .nav-toggler.open .start {
  transform: rotate(-3198deg) translate(20.5px, 10px);
}
.header .header-items #nav-actions .nav-toggler.open .end {
  transform: rotate(-3198deg) translate(-20px, -10px);
}
.header .header-items #nav-actions .nav-toggler .bar {
  width: 100%;
  height: 3px;
  border-radius: 3px;
  background: #14120f;
  transition: all 700ms cubic-bezier(1, 0.1, 0.2, 1);
}
.header .header-items #nav-actions .nav-toggler .bar:nth-child(odd) {
  width: 60%;
}
.header .header-items #nav-actions .nav-toggler .bar.start {
  align-self: flex-start;
  transform-origin: right;
}
.header .header-items #nav-actions .nav-toggler .bar.end {
  align-self: flex-end;
  transform-origin: left;
}

