@use 'config' as c;

@mixin small-PCs {
  @media screen and (max-width: 1090px) {
    .products {
      #{&}-brands {
        & img {
          width: 120px;
          cursor: pointer;

          &:not(:last-child) {
            margin-right: 20px;
          }
        }
      }
    }

    #interior {
      padding: 0 35px;
      margin-bottom: 50px;
    }

    .interior-container {
      width: 100%;

      & .interior-left {
        display: none;
      }

      & .interior-right {
        width: 100%;
        @include c.flex(row);
        & .interior-copy {
          width: 100%;

          & h4 {
            font-size: map-get(c.$font-sizes, section-heading-texts);
          }

          & p {
            font-size: map-get(c.$font-sizes, main-contents);
          }
        }

        & .interior-right-img {
          margin-left: 50px;
          width: 500px;
        }
      }
    }
  }
}

@mixin tablets {
  @media screen and (max-width: 768px) {
    .arrow {
      display: none;
    }
    .header {
      // padding: 30px 0;
      & .header-items {
        & #nav-list {
          @include c.flex(row);
          width: 300px;
        }
      }
    }

    .hero {
      @include c.flex(column);
      padding: 0 35px;

      & .hero-contents {
        position: relative;
        width: 100%;
        margin-right: 0px;
        text-align: center;
        margin-bottom: 50px;
        & .hero-heading {
          font-size: map-get(c.$font-sizes, hero-main-text-mobile);
        }

        & .hero-copy {
          font-size: map-get(c.$font-sizes, main-contents-mobile);
          margin: 10px 0 20px;
        }
      }

      & .hero-img-container {
        width: 100%;
        padding-right: 40px;
        & .hero-imgs {
          width: 100%;
          @include c.flex(row);

          & img {
            width: 33.33%;
            &:not(:last-child) {
              margin-right: 20px;
            }
            &:nth-child(odd) {
              transform: translateY(50px);
            }
          }
        }
      }
    }

    .products {
      position: relative;
      padding: 0 35px;
      &:after {
        position: absolute;
        content: '';
        width: 30px;
        height: 70px;
        background: white;
        filter: blur(10px);
        bottom: 0;
        left: -5px;
      }
      &:before {
        position: absolute;
        content: '';
        width: 30px;
        height: 70px;
        background: white;
        filter: blur(10px);
        bottom: 0;
        right: -5px;
        z-index: 2;
      }

      #{&}-number {
        font-size: map-get(c.$font-sizes, hero-main-text-mobile);

        & span {
          margin-top: -10px;
          font-size: map-get(c.$font-sizes, main-contents-mobile);
        }
      }

      #{&}-brands {
        animation: slide 10s linear infinite;

        & img {
          width: 120px;
          cursor: pointer;

          &:not(:last-child) {
            margin-right: 20px;
          }
        }
      }
    }

    @keyframes slide {
      0% {
        transform: translateX(0%);
      }

      50% {
        transform: translateX(-25%);
      }

      100% {
        transform: translateX(0%);
      }
    }

    .products-desc {
      padding: 0 35px;

      #{&}-container {
        @include c.flex(column);

        & .products-desc-contents {
          text-align: center;
          width: 100%;

          & .products-desc-heading {
            font-size: map-get(c.$font-sizes, section-heading-texts-mobile);
          }

          & .products-desc-copy {
            font-size: map-get(c.$font-sizes, main-contents-mobile);
            margin: 12px 0;
          }
        }

        & .products-desc-img {
          width: 438px;
          margin-top: -30px;
        }
      }

      & .products-shelf {
        flex-direction: row-reverse;
        @include c.flex(column);
        margin-top: -20px;

        & .products-desc-img {
          width: 300px;
          margin-right: 0px;
          margin-top: 30px;
        }
      }
    }

    #products-display {
      & .products-display-heading {
        & h4 {
          font-size: map-get(c.$font-sizes, section-heading-texts-mobile);
        }
      }
    }

    #interior {
      margin-bottom: 50px;
    }

    .interior-container {
      width: 100%;

      & .interior-right {
        width: 100%;
        @include c.flex(column);
        & .interior-copy {
          text-align: center;
          width: 100%;

          & h4 {
            font-size: map-get(c.$font-sizes, section-heading-texts);
          }

          & p {
            font-size: map-get(c.$font-sizes, main-contents);
            margin: 20px 0;
          }
        }

        & .interior-right-img {
          margin-left: 0px;
          width: 100%;
        }
      }
    }

    #testimonials {
      padding: 40px 30px;
    }
    .testimonial-container {
      & h4 {
        font-size: map-get(c.$font-sizes, section-heading-texts-mobile);
        text-align: center;
      }
    }
    .testimonials {
      width: 95%;

      & .testimonial {
        padding: 30px 10px;

        & .testimonial-img {
          &::before {
            position: absolute;
            content: '';
            width: 20px;
            height: 20px;
            border-radius: 50%;
            background: rgba(0, 0, 0, 0.7);
            filter: blur(10px);
            top: 16px;
            left: 5px;
            z-index: 1;
          }

          & img {
            width: 30px;
          }
        }

        & .testimonial-content {
          text-align: center;
          font-size: 15px;
          margin: 20px 0 35px;
        }

        & .testimonial-person {
          display: flex;
          align-items: center;

          & .person-img {
            margin-right: 10px;
            width: 35px;
            & img {
              width: 100%;
            }
          }

          & .person-details {
            & h5 {
              font-size: 12px;
            }
            & p {
              font-size: 8px;
              line-height: 1;
            }
          }
        }
      }
    }
    .slider-btns {
      align-self: center;
      margin-top: 50px;
      @include c.flex(row);

      & .slider-btn {
        color: c.$maincolor;
        border: 1px solid c.$maincolor;
        border-radius: 50%;
        height: 35px;
        width: 35px;
        line-height: 35px;
        text-align: center;
        cursor: pointer;
        &:not(:first-child) {
          margin-left: 20px;
        }

        &:hover {
          background: c.$maincolor;
          color: white;
        }

        & i {
          font-size: 20px;
          font-weight: bolder;
        }
      }
    }

    #newsletter {
      padding: 0 30px;
    }
    .newsletter-container {
      width: 80%;
      & h4 {
        font-size: map-get(c.$font-sizes, section-heading-texts);
        @include c.secFont;
        line-height: 50px;
      }

      & p {
        font-size: map-get(c.$font-sizes, main-contents);
        margin: 30px auto;
        width: 100%;
      }

      & form {
        width: 100%;
        @include c.flex(column);

        & input {
          width: 100%;
        }
        & button {
          width: 100%;
          margin-left: 0px;
          margin-top: 10px;
        }
      }
    }

    footer {
      padding: 50px 30px;
    }

    .top-footer {
      @include c.flex(column);
      align-items: flex-start;

      .footer-heading {
        margin-bottom: 20px;
      }
      & .footer-left {
        & .footer-texts {
          width: 100%;
        }
      }

      & .footer-right {
        margin-top: 50px;
        width: 100%;

        & .footer-item {
          &:not(:last-child) {
            margin-right: 10px;
          }

          & .footer-lists {
            & .footer-sub {
              &:not(:last-child) {
                margin-bottom: 8px;
              }
            }
          }
        }
      }
    }
  }
}

@mixin mobiles {
  @media screen and (max-width: 500px) {
    .header {
      @include c.transition;
      transition-delay: 200ms;
      background: transparent;
      z-index: 12;
      padding: 0;
      height: 58px;

      &.open {
        height: 100%;
        width: 100%;
      }

      & .header-items {
        // position: relative;
        // z-index: 20;
        background: c.$sec-color;
        max-width: 1400px;
        width: 100%;
        margin: 0 auto;
        // padding: 20px;
        // padding-left: 30px;
        // padding-right: 30px;
        padding: 15px 35px;
        color: c.$dark-color;

        & #nav-list {
          position: absolute;
          background: c.$sec-color;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          @include c.flex(column);
          transform: translateY(-200%);
          transition: all 750ms ease-in-out;
          align-items: center;
          justify-content: center;
          // transition-delay: 0.5s;
          overflow: hidden;
          // z-index: 50;

          &.open {
            transform: translateY(0);
            height: 100%;
          }

          & .nav-item {
            font-size: 30px;
            transform: translateY(-1000%);
            opacity: 0;
            transition: all 1200ms cubic-bezier(0.2, 0.5, 0.9, 1);

            &.slide {
              transform: translateY(0);
              opacity: 1;
            }

            &.slide:nth-child(even) {
              transform: translateX(30%);
            }
            &.slide:nth-child(odd) {
              transform: translateX(-30%);
            }

            &:not(:last-child) {
              margin-bottom: 60px;
            }
          }
        }

        & #nav-actions {
          position: relative;
          @include c.flex(row);
          // z-index: 55;

          & i {
            cursor: pointer;
            @include c.transition;

            &:hover {
              color: c.$dark-milk;
            }
          }

          & i:nth-child(2) {
            margin-left: 10px;
          }

          & .nav-toggler {
            width: 30px;
            height: 25px;
            cursor: pointer;
            @include c.flex(column);
            margin-left: 20px;
          }
        }
      }
    }

    #hero-main {
      padding-top: 30px;
    }

    .hero {
      & .hero-contents {
        & .hero-heading {
          font-size: 45px;
        }

        & .hero-copy {
          font-size: 18px;
        }

        & .hero-btn {
          & a {
            font-size: 16px;
          }
        }
      }

      & .hero-img-container {
        width: 100%;
