      }
    }

    .products {
      padding: 0 35px;
      align-items: center;

      #{&}-number {
        font-size: map-get(c.$font-sizes, hero-main-text-mobile);

        & span {
          margin-top: -10px;
          font-size: map-get(c.$font-sizes, main-contents-mobile);
        }
      }

      #{&}-brands {
        & img {
          width: 120px;
          cursor: pointer;

          &:not(:last-child) {
            margin-right: 30px;
          }
        }
      }
    }

    @keyframes slide {
      0% {
        transform: translateX(0%);
      }

      50% {
        transform: translateX(-65%);
      }

      100% {
        transform: translateX(0%);
      }
    }

    .products-desc {
      padding: 0 35px;

      #{&}-container {
        @include c.flex(column);

        & .products-desc-contents {
          text-align: center;
          width: 100%;

          & .products-desc-heading {
            font-size: map-get(c.$font-sizes, section-heading-texts-mobile);
          }

          & .products-desc-copy {
            font-size: map-get(c.$font-sizes, main-contents-mobile);
            margin: 12px 0;
          }

          & .products-desc-btn {
            & a {
              font-size: map-get(c.$font-sizes, btn-texts-mobile);

              & span {
                margin-left: 10px;
              }
            }
          }
        }

        & .products-desc-img {
          width: 350px;
          margin-top: -30px;
        }
      }

      & .products-shelf {
        flex-direction: row-reverse;
        @include c.flex(column);
        margin-top: 50px;

        & .products-desc-img {
          width: 230px;
          margin-right: 0px;
          margin-top: 30px;
        }
      }
    }

    #products-display {
      & .products-display-heading {
        @include c.flex(column);
        text-align: center;
        & h4 {
          font-size: map-get(c.$font-sizes, section-heading-texts-mobile);
          margin-bottom: 20px;
        }

        & p {
          background: c.$dark-milk;
