          }
          & a {
            pointer-events: none;
          }
        }
      }

      .products-display-imgs {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
        width: 100%;
        row-gap: 25px;

        & .products-display-img {
          @include c.flex(column);

          & img {
            position: relative;
            width: 250px;
