    .testimonials {
      position: relative;
      height: 250px;
      width: 100%;
      @include c.flex(column);
      justify-content: center;

      & .testimonial {
        position: absolute;
        padding: 30px 10px;
        width: 60%;
        margin: 0 auto;
        transform: scale(0);
        transition: all 2200ms cubic-bezier(0.2, 0.5, 0.2, 1);

        &.active {
          transform: scale(1);
          width: 80%;
          z-index: 4;
        }

        & .testimonial-img {
          &::before {
            width: 30px;
            height: 30px;
          }

          & img {
            width: 40px;
          }
        }

        & .testimonial-content {
