        height: 15px;
        border-radius: 50%;
        border: 1px solid c.$maincolor;
        transition: all 1000ms ease-in-out;
        cursor: pointer;
        &.active {
          background: c.$maincolor;
        }
      }
    }
    .slider-btns {
      margin-top: 10px;
    }

    #newsletter {
      padding: 0 30px;
    }
    .newsletter-container {
      width: 100%;
      & h4 {
        font-size: map-get(c.$font-sizes, section-heading-texts-mobile);
        @include c.secFont;
        line-height: 50px;
      }

      & p {
        font-size: map-get(c.$font-sizes, main-contents-mobile);
        margin: 30px auto;
        width: 100%;
      }

      & form {
        width: 100%;
        @include c.flex(column);

        & input {
          width: 100%;
        }
        & button {
          width: 100%;
          margin-left: 0px;
          margin-top: 10px;
        }
      }
    }

    footer {
      padding: 50px 30px;
    }

    .top-footer {
      @include c.flex(column);
      align-items: flex-start;

      .footer-heading {
        margin-bottom: 10px;
      }
      & .footer-left {
        & .footer-texts {
          width: 100%;
        }
      }

      & .footer-right {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        grid-gap: auto;
        margin-top: 50px;
        width: 100%;

        & .footer-discover,
        & .footer-contact {
          margin-top: 25px;
        }

        & .footer-item {
          &:not(:last-child) {
            margin-right: 0px;
          }
        }
      }
    }
    .bottom-footer {
      margin-top: 70px;

      & .media {
        & i {
          &:not(:last-child) {
            margin-right: 10px;
          }
        }
      }
    }
  }
}

@mixin small-mobiles {
  @media screen and (max-width: 420px) {
    @keyframes slide {
      0% {
        transform: translateX(0%);
      }

      50% {
        // transform: translateX(-120%);
        transform: translateX(-100vw);
      }

      100% {
        transform: translateX(0%);
      }
    }

    #products-display {
      .products-display-imgs {
        margin-top: 20px;
        grid-template-columns: repeat(2, 1fr);
        grid-gap: 10px;

        & .third,
        & .fourth {
          margin-top: 10px;
        }
        & .products-display-img {
          & img {
            width: 100%;
          }

          & p {
            font-size: 15px;
          }
        }
      }
    }

    .bottom-footer {
      margin-top: 70px;
      @include c.flex(column);
      align-items: flex-start;

      & .media {
        margin-top: 10px;
        & i {
          &:not(:last-child) {
            margin-right: 10px;
          }
        }
      }
    }
  }
}

@mixin x-small-mobiles {
  @media screen and (min-width: 200px) and (max-width: 370px) {
    @keyframes slide {
      0% {
        transform: translateX(0%);
      }

      50% {
        transform: translateX(-155%);
      }

      100% {
        transform: translateX(0%);
      }
    }
  }
}

@for $i from 1 through 4 {
  .header {
    & .header-items {
      & #nav-list {
        & .nav-item:nth-child(#{$i}) {
          transition-delay: ($i * 0.1s);
        }
      }
    }
  }
}

