@use 'config' as c;
@use 'screen' as s;

[data-aos] {
  body[data-aos-easing='new-easing'] &,
  &[data-aos][data-aos-easing='new-easing'] {
    transition-timing-function: cubic-bezier(0.25, 0.25, 0.75, 0.75);
  }
}
body {
  background: white;
  font-family: 'Nunito', sans-serif;
}

.arrow {
  margin-top: -100px;
  width: 500px;
  & img {
    width: 100%;
  }
}
.arrow.right {
  margin-left: auto;
}

.preloader {
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  align-self: auto;
  background: c.$sec-color;
  @include c.flex(column);
  justify-content: center;
  @include c.secFont;
  font-size: 60px;
  font-weight: bolder;
  color: c.$maincolor;
  z-index: 100;
  transform: translateY(0);
  @include c.transition;
  transition: all 10000ms ease-out;
  &.remove {
    transform: translateY(-2000%);
  }

  & .lds-ellipsis {
    display: inline-block;
    position: relative;
    width: 80px;
    height: 80px;
  }
  .lds-ellipsis div {
    position: absolute;
    top: 33px;
    width: 15px;
    height: 15px;
    border-radius: 30%;
    background: c.$maincolor;
    animation-timing-function: cubic-bezier(0, 1, 1, 0);
  }
  .lds-ellipsis div:nth-child(1) {
    left: 8px;
    animation: lds-ellipsis1 0.9s infinite;
  }
  .lds-ellipsis div:nth-child(2) {
    left: 8px;
    animation: lds-ellipsis2 0.9s infinite;
  }
  .lds-ellipsis div:nth-child(3) {
    left: 32px;
    animation: lds-ellipsis2 0.9s infinite;
  }
  .lds-ellipsis div:nth-child(4) {
    left: 56px;
    animation: lds-ellipsis3 0.9s infinite;
  }
}

@keyframes lds-ellipsis1 {
  0% {
    transform: scale(0);
  }
  100% {
    transform: scale(1);
  }
}
@keyframes lds-ellipsis3 {
  0% {
    transform: scale(1);
  }
  100% {
    transform: scale(0);
  }
}
@keyframes lds-ellipsis2 {
  0% {
    transform: translate(0, 0);
  }
  100% {
    transform: translate(24px, 0);
  }
}

.to-top {
  position: absolute;
  bottom: 20px;
  right: 20px;
  color: c.$sec-color;
  background: c.$maincolor;
  border-radius: 50%;
  height: 30px;
  line-height: 30px;
  text-align: center;
  cursor: pointer;
  z-index: 5;
  position: fixed;
  animation: bulge infinite 2000ms;
  opacity: 0;
  width: 0;
  @include c.transition;
  &.show {
    opacity: 0.8;
    width: 30px;
  }

  & i {
    font-size: 25px;
    font-weight: bold;
  }
}
@keyframes bulge {
  0% {
    transform: scale(1);
  }

  50% {
    transform: scale(1.2);
  }

  100% {
    transform: scale(1);
  }
}

.header {
  position: fixed;
  // height: 58px;
  width: 100%;
  // height: 100vh;
  top: 0;
  font-family: 'Plus Jakarta Display', sans-serif;
  background: c.$sec-color;
  padding: 12px 0;
  z-index: 12;
  &.bottom-border {
    box-shadow: -3px 3px 8px grey;
  }
  & .header-items {
    max-width: 1300px;
    width: 90%;
    margin: 0 auto;
    color: c.$dark-color;
    @include c.flex(row);

    & #logo {
      font-size: map-get(c.$font-sizes, main-contents);
      font-weight: map-get(c.$font-weight, bold);
      cursor: pointer;
      @include c.transition;

      &:hover {
        color: c.$dark-milk;
      }
    }

    & #nav-list {
      @include c.flex(row);
      width: 400px;

      & .nav-item {
        position: relative;
        font-size: map-get(c.$font-sizes, nav-footer-texts);

        &.show::after {
          position: absolute;
          content: '';
          bottom: -2px;
          left: 50%;
          transform: translateX(-50%);
          width: 75%;
          height: 2px;
          background: c.$dark-color;
        }

        &:before {
          position: absolute;
          content: '';
          bottom: -2px;
          left: 50%;
          transform: translateX(-50%);
          width: 0;
          height: 2px;
          background: c.$dark-color;
          @include c.transition;
        }
        &:hover::before {
          width: 75%;
        }
      }
    }

    & #nav-actions {
      @include c.flex(row);
      position: relative;
      z-index: 20;
      & i {
        cursor: pointer;
        @include c.transition;

        &:hover {
          color: c.$dark-milk;
        }
      }

      & i:nth-child(2) {
        margin-left: 15px;
      }

      & .nav-toggler {
        width: 30px;
        height: 25px;
        cursor: pointer;
        @include c.flex(column);
        display: none;

        &:hover > div {
          background: c.$dark-milk;
        }

        &.open .bar {
          transform: rotate(-42deg);
        }
        &.open .start {
          transform: rotate(-3198deg) translate(20.5px, 10px);
        }
        &.open .end {
          // transform: rotate(-500deg) translate(0px, 8px);
          transform: rotate(-3198deg) translate(-20px, -10px);
        }

        & .bar {
          width: 100%;
          height: 3px;
          border-radius: 3px;
          background: c.$dark-color;
          transition: all 700ms cubic-bezier(1, 0.1, 0.2, 1);

          &:nth-child(odd) {
            width: 60%;
          }

          &.start {
            align-self: flex-start;
            transform-origin: right;
          }
          &.end {
            align-self: flex-end;
            transform-origin: left;
          }
        }
      }
    }
  }
}

